/*  83  /2 RMBoth  ib op32  | Add with CF sign-extended imm8 into r/m32. */
inline int adc (code_ptr p,
         const DwordReg_m_EAX (&   modrm_rm),
         imm8_t imm)
{
    return encode_instruction (p,
                               Prefix (Op32,
                                       AddrNothing,
                                       modrm_rm),
                               rexByte (rex_modrm_rm (modrm_rm)),
                               Code (0x83),
                               make_modrm (modrm_rm,2),
                               make_imm (imm));
}
