/*  C1  /0 RMBoth  ib op32  | Rotate 32 bits r/m32 left imm8 times. */
inline int rol (code_ptr p,
         const DwordReg (&   modrm_rm),
         imm8_t imm)
{
    if (imm == 1)
       {
           return encode_instruction (p,
                                      Prefix (Op32,
                                              AddrNothing,
                                              modrm_rm),
                                      rexByte (rex_modrm_rm (modrm_rm)),
                                      Code (0xd1),
                                      make_modrm (modrm_rm,0));
       }
    else
       {
           return encode_instruction (p,
                                      Prefix (Op32,
                                              AddrNothing,
                                              modrm_rm),
                                      rexByte (rex_modrm_rm (modrm_rm)),
                                      Code (0xc1),
                                      make_modrm (modrm_rm,0),
                                      make_imm (imm));
       }
}
